import axios from "axios";
import React from "react";

const AddPost = () => {
  const saveToDB = (event) => {
    event.preventDefault();

    console.log(event.target["author"].value);
    console.log(event.target["title"].value);

    let bodyFormData = new FormData();
    bodyFormData.append("author", event.target["author"].value);
    bodyFormData.append("title", event.target["title"].value);

    axios({
      method: "post",
      url: "/posts",
      data: bodyFormData.stringify(),
      headers: { "Content-Type": "Application/json" },
    })
      .then(() => {
        console.log("form data has been saved succesfully");
        console.log("force re-render here");
      })
      .catch((err) => {
        console.log(err.message);
      });
  };
  return (
    <div className="addPost">
      <form onSubmit={saveToDB}>
        <input type="text" name="author" required />
        <input type="text" name="title" required />
        <input type="submit" value="Add Post" />
      </form>
    </div>
  );
};

export default AddPost;
