import React, { useState, Component } from "react";
import Post from "./Post";

function Posts() {
  let posts = [
    { id: 123, title: "banana one", author: "wuff" },
    { id: 321, title: "fruitt one", author: "meaw" },
    { id: 534, title: "health one", author: "ding" },
    { id: 674, title: "inject one", author: "ding" },
  ];

  let [selectedPost, setSelectedPost] = useState(null);

  const updateSelectedPost = (selectedPostId) => {
    setSelectedPost(selectedPostId);
    console.log(posts.at(selectedPost));
  };

  return (
    <div className="posts">
      {posts.map((post, key) => (
        <Post key={key} post={post} setSelectedPost={updateSelectedPost} />
      ))}
    </div>
  );
}

export default Posts;
